import { useSelector } from "react-redux";
import GraphBar from "./GraphBar";

function SpentPerDay() {
  const spent = [useSelector((state) => state.moneySpent.value)];

  let reduxProcess = spent
  .toString()
  .split(",")
  .map((str) => {
    //process redux data
    return Number(str);
  });

  let days = ["mon", "tue", "wed", "thur", "fri", "sat", "sun"];

  let id = [1, 2, 3, 4, 5, 6, 7]

  const dayIncome = id.map((id, index) => {
    let dataObj = {};
    dayIncome.id = id;
    dayIncome.day = days[index];
    dayIncome.spent = reduxProcess[index];
    return dataObj;
  })

  const b = 24; //available heights: 0, 2, 4, 6, 8, 9, 12, 16, 20, 24
  
  const c = "w-6 bg-terracotta-500 rounded-sm hover:bg-neptune-500 h-" + b; //test height variable
  
  return (
    <div className="w-full flex items-end h-fit mt-6">
      <div className="flex flex-row items-end justify-around w-full h-fit">
        
        {dayIncome.map((props) => {
          return <GraphBar key = {props.id} day/>;
        })}

        {/*<div
          className="flex flex-col justify-center items-center"
        >
          <div
            className="w-6 h-16 bg-terracotta-500 rounded-sm hover:bg-neptune-500"
          ></div>
          <span>sun</span>
        </div>*/}

      </div>
    </div>
  );
}

export default SpentPerDay;
