import { useState } from 'react';
import './App.css';
import MainComp from './Components/MainComp';
import TopComp from './Components/TopComp';

function App() {
const [show, setShow] = useState(0);

  return (
    <div className='bg-linen-500 h-screen w-screen flex flex-col items-center justify-center'>
      <TopComp show={show}/>
      <MainComp show={show}/>
      {!show ? (
        <div className="h-12 w-72 bg-terracotta-600  flex flex-row items-center justify-center rounded-md cursor-pointer mt-6 hover:bg-terracotta-500 hover:shadow-sambuca-300 hover:shadow-lg mb-8">
          <div >Calculate</div>
        </div>
      ) : null}
    </div>
  );
}

export default App;
