import  { createSlice } from '@reduxjs/toolkit'

const totMoneySlice = createSlice({
    name: 'totMoney',
    initialState: {
        value: 1
      },

      reducers: {
        update: (state, action) => {
            state.value = action.payload.value
        }
      }
})

export const { update } = totMoneySlice.actions
export default totMoneySlice.reducer;
